import { builtinI18nSchema } from '@astrojs/starlight/schemas/i18n';
import cs from '@astrojs/starlight/translations/cs.json';
import en from '@astrojs/starlight/translations/en.json';
import es from '@astrojs/starlight/translations/es.json';
import de from '@astrojs/starlight/translations/de.json';
import ja from '@astrojs/starlight/translations/ja.json';
import pt from '@astrojs/starlight/translations/pt.json';
import fa from '@astrojs/starlight/translations/fa.json';
import fr from '@astrojs/starlight/translations/fr.json';
import gl from '@astrojs/starlight/translations/gl.json';
import he from '@astrojs/starlight/translations/he.json';
import id from '@astrojs/starlight/translations/id.json';
import it from '@astrojs/starlight/translations/it.json';
import nl from '@astrojs/starlight/translations/nl.json';
import da from '@astrojs/starlight/translations/da.json';
import tr from '@astrojs/starlight/translations/tr.json';
import ar from '@astrojs/starlight/translations/ar.json';
import nb from '@astrojs/starlight/translations/nb.json';
import zh from '@astrojs/starlight/translations/zh-CN.json';
import ko from '@astrojs/starlight/translations/ko.json';
import sv from '@astrojs/starlight/translations/sv.json';
import ro from '@astrojs/starlight/translations/ro.json';
import ru from '@astrojs/starlight/translations/ru.json';
import vi from '@astrojs/starlight/translations/vi.json';
import uk from '@astrojs/starlight/translations/uk.json';
import hi from '@astrojs/starlight/translations/hi.json';
import zhTW from '@astrojs/starlight/translations/zh-TW.json';
import pl from '@astrojs/starlight/translations/pl.json';

const { parse } = builtinI18nSchema();

export default Object.fromEntries(
	Object.entries({
		cs,
		en,
		es,
		de,
		ja,
		pt,
		fa,
		fr,
		gl,
		he,
		id,
		it,
		nl,
		da,
		tr,
		ar,
		nb,
		zh,
		ko,
		sv,
		ro,
		ru,
		vi,
		uk,
		hi,
		'zh-TW': zhTW,
		pl,
	}).map(([key, dict]) => [key, parse(dict)])
);
